
const norc = require('./norc.js')

norc.changeStore(() => {
	let lastTs = 0
	return (ts) => new Promise(resolve => {
		if (ts === lastTs) {
			return resolve(false)
		}
		lastTs = ts
		return resolve(true)
	})
})

norc.addJob('wakeUp', {
	dow: '1-5',			// weekday
	hour: 7,
	minute: 30,
}, () => {
	console.log('Rise and shine!')
})
